parameters.jsCode:
// Extract client info from email
const emailSubject = $json.subject || '';
const emailBody = $json.bodyText || $json.snippet || '';
const fromEmail = $json.from || '';

// Parse client information (adjust parsing based on your email format)
const clientInfo = {
  email: fromEmail.match(/<(.+)>/)?.[1] || fromEmail,
  name: fromEmail.split('<')[0].trim() || 'Unknown',
  sport: 'tennis', // Default for now
  teamName: 'Team', // Will be updated manually
  contactMethod: 'email',
  onboardingDate: new Date().toISOString(),
  status: 'new',
  clientId: `client_${Date.now()}`,
  originalEmail: {
    subject: emailSubject,
    body: emailBody,
    messageId: $json.id
  }
};

// Extract any mentioned social media handles or team names from email
const instagramMatch = emailBody.match(/@([a-zA-Z0-9._]+)/g);
const teamNameMatch = emailBody.match(/(team|club|academy)\s+([a-zA-Z\s]+)/i);

if (instagramMatch) {
  clientInfo.socialHandles = {
    instagram: instagramMatch[0]
  };
}

if (teamNameMatch) {
  clientInfo.teamName = teamNameMatch[2].trim();
}

return [{ json: clientInfo }];

