// Parse platform results and prepare for analytics collection
const clientInfo = $('get-active-clients').item;
const posts = $json;
const analyticsData = [];

posts.forEach(post => {
  try {
    // Parse platform results JSON
    const platformResults = JSON.parse(post.platform_results || '{}');

    // Extract post IDs for different platforms
    const postData = {
      clientId: clientInfo.client_id,
      clientName: clientInfo.name,
      teamName: clientInfo.team_name,
      postId: post.timestamp, // Using timestamp as unique identifier
      content: post.content,
      postType: post.post_type,
      publishedAt: post.published_at,
      platforms: post.platforms?.split(',') || [],
      instagramPostId: null,
      twitterPostId: null,
      facebookPostId: null,
      contentSheetId: clientInfo.content_sheet_id,
      originalRowData: post
    };

    // Try to extract platform-specific IDs from results
    if (platformResults.instagram?.id) {
      postData.instagramPostId = platformResults.instagram.id;
    }
    if (platformResults.twitter?.data?.id) {
      postData.twitterPostId = platformResults.twitter.data.id;
    }
    if (platformResults.facebook?.id) {
      postData.facebookPostId = platformResults.facebook.id;
    }

    analyticsData.push(postData);
  } catch (error) {
    console.log(`Error parsing post data: ${error.message}`);
  }
});

return analyticsData.map(data => ({ json: data }));

// Combine analytics data from all platforms
const originalData = $('prepare-analytics-data').item;
const instagramData = $('get-instagram-analytics').first()?.json;
const twitterData = $('get-twitter-analytics').first()?.json;
const facebookData = $('get-facebook-analytics').first()?.json;

// Initialize analytics object
const analytics = {
  ...originalData,
  collectionDate: new Date().toISOString(),
  platforms: {
    instagram: null,
    twitter: null,
    facebook: null
  },
  totalEngagement: 0,
  totalReach: 0,
  totalImpressions: 0,
  bestPerformingPlatform: null
};

// Process Instagram data
if (instagramData && !instagramData.error) {
  analytics.platforms.instagram = {
    likes: instagramData.like_count || 0,
    comments: instagramData.comments_count || 0,
    shares: instagramData.shares_count || 0,
    reach: instagramData.reach || 0,
    impressions: instagramData.impressions || 0,
    engagement: (instagramData.like_count || 0) + (instagramData.comments_count || 0) + (instagramData.shares_count || 0),
    permalink: instagramData.permalink
  };
}

// Process Twitter data
if (twitterData?.data && !twitterData.error) {
  const metrics = twitterData.data.public_metrics || {};
  analytics.platforms.twitter = {
    likes: metrics.like_count || 0,
    retweets: metrics.retweet_count || 0,
    replies: metrics.reply_count || 0,
    quotes: metrics.quote_count || 0,
    impressions: metrics.impression_count || 0,
    engagement: (metrics.like_count || 0) + (metrics.retweet_count || 0) + (metrics.reply_count || 0) + (metrics.quote_count || 0)
  };
}

// Process Facebook data
if (facebookData && !facebookData.error) {
  analytics.platforms.facebook = {
    reactions: facebookData.reactions?.summary?.total_count || 0,
    comments: facebookData.comments?.summary?.total_count || 0,
    shares: facebookData.shares?.count || 0,
    engagement: (facebookData.reactions?.summary?.total_count || 0) + (facebookData.comments?.summary?.total_count || 0) + (facebookData.shares?.count || 0),
    permalink: facebookData.permalink_url
  };
}

// Calculate totals
Object.values(analytics.platforms).forEach(platform => {
  if (platform) {
    analytics.totalEngagement += platform.engagement || 0;
    analytics.totalReach += platform.reach || 0;
    analytics.totalImpressions += platform.impressions || 0;
  }
});

// Find best performing platform
let maxEngagement = 0;
Object.entries(analytics.platforms).forEach(([platformName, data]) => {
  if (data && data.engagement > maxEngagement) {
    maxEngagement = data.engagement;
    analytics.bestPerformingPlatform = platformName;
  }
});

return [{ json: analytics }];

