// Parse template update request from email
const emailSubject = $json.subject || '';
const emailBody = $json.bodyText || $json.snippet || '';
const fromEmail = $json.from || '';

// Extract template information
const templateInfo = {
  requestId: `template_${Date.now()}`,
  fromEmail: fromEmail.match(/<(.+)>/)?.[1] || fromEmail,
  fromName: fromEmail.split('<')[0].trim() || 'Unknown',
  subject: emailSubject,
  body: emailBody,
  requestDate: new Date().toISOString(),
  status: 'pending',
  templateType: 'custom',
  clientId: null,
  originalMessageId: $json.id
};

// Try to identify template type from subject/body
if (emailSubject.toLowerCase().includes('pre-match') || emailBody.toLowerCase().includes('pre-match')) {
  templateInfo.templateType = 'pre-match';
} else if (emailSubject.toLowerCase().includes('match day') || emailBody.toLowerCase().includes('match day')) {
  templateInfo.templateType = 'match-day';
} else if (emailSubject.toLowerCase().includes('result') || emailBody.toLowerCase().includes('result')) {
  templateInfo.templateType = 'match-result';
} else if (emailSubject.toLowerCase().includes('practice') || emailBody.toLowerCase().includes('practice')) {
  templateInfo.templateType = 'practice';
} else if (emailSubject.toLowerCase().includes('motivational') || emailBody.toLowerCase().includes('motivational')) {
  templateInfo.templateType = 'motivational';
}

// Try to extract client ID from email
const clientIdMatch = emailBody.match(/client[_\s]*id[_\s]*:?[_\s]*(\w+)/i);
if (clientIdMatch) {
  templateInfo.clientId = clientIdMatch[1];
}

// Extract any specific template content
const templateContentMatch = emailBody.match(/template[_\s]*content[_\s]*:([\s\S]*?)(?:\n\n|$)/i);
if (templateContentMatch) {
  templateInfo.templateContent = templateContentMatch[1].trim();
}

return [{ json: templateInfo }];

