parameters.jsCode:
// Analyze template usage and performance
const templates = $json;
const weekAgo = new Date(Date.now() - 7 * 24 * 60 * 60 * 1000);

// Group by type and analyze usage
const typeStats = {};
const lowUsageTemplates = [];
const highUsageTemplates = [];
const newTemplates = [];

templates.forEach(template => {
  const type = template.template_type;
  const usage = parseInt(template.usage_count) || 0;
  const createdDate = new Date(template.created_date);

  // Initialize type stats
  if (!typeStats[type]) {
    typeStats[type] = {
      totalTemplates: 0,
      totalUsage: 0,
      averageUsage: 0,
      mostUsed: { content: '', usage: 0 },
      leastUsed: { content: '', usage: 999 }
    };
  }

  typeStats[type].totalTemplates++;
  typeStats[type].totalUsage += usage;

  // Track most and least used
  if (usage > typeStats[type].mostUsed.usage) {
    typeStats[type].mostUsed = {
      content: template.content.substring(0, 60) + '...',
      usage
    };
  }
  if (usage < typeStats[type].leastUsed.usage) {
    typeStats[type].leastUsed = {
      content: template.content.substring(0, 60) + '...',
      usage
    };
  }

  // Categorize templates
  if (createdDate > weekAgo) {
    newTemplates.push({
      type,
      content: template.content.substring(0, 80) + '...',
      createdBy: template.created_by
    });
  }

  if (usage === 0 && createdDate < weekAgo) {
    lowUsageTemplates.push({
      templateId: template.template_id,
      type,
      content: template.content.substring(0, 60) + '...',
      daysSinceCreated: Math.floor((new Date() - createdDate) / (1000 * 60 * 60 * 24))
    });
  }

  if (usage > 10) {
    highUsageTemplates.push({
      templateId: template.template_id,
      type,
      content: template.content.substring(0, 60) + '...',
      usage
    });
  }
});

// Calculate averages
Object.keys(typeStats).forEach(type => {
  typeStats[type].averageUsage = Math.round(typeStats[type].totalUsage / typeStats[type].totalTemplates);
});

return [{
  json: {
    reportDate: new Date().toISOString().split('T')[0],
    totalTemplates: templates.length,
    typeStats,
    newTemplates: newTemplates.slice(0, 5), // Top 5 new
    lowUsageTemplates: lowUsageTemplates.slice(0, 5), // Top 5 unused
    highUsageTemplates: highUsageTemplates.slice(0, 5), // Top 5 popular
    recommendations: {
      createMore: Object.entries(typeStats).sort((a, b) => b[1].averageUsage - a[1].averageUsage)[0]?.[0] || 'none',
      reviewUnused: lowUsageTemplates.length,
      totalTypes: Object.keys(typeStats).length
    }
  }
}];

