// Universal content decision engine for any SMB type
const calendar = $('get-content-calendar').all();
const availableImages = $('check-for-images').all();
const now = new Date();
const todayStr = now.toISOString().split('T')[0];
const hour = now.getHours();
const dayOfWeek = now.getDay(); // 0=Sunday, 6=Saturday

// Client configuration (loaded from sheet or hardcoded)
const clientConfig = {
  businessType: $json.businessType || 'general', // restaurant, retail, service, professional, fitness, etc.
  contentTypes: $json.contentTypes?.split(',') || ['promotional', 'educational', 'engagement', 'announcement'],
  postingSchedule: $json.postingSchedule || 'daily',
  specialDays: $json.specialDays?.split(',') || [], // Monday=special, Friday=feature, etc.
  requiresImages: $json.requiresImages !== 'false',
  tone: $json.tone || 'professional_friendly'
};

const postsToCreate = [];

// Check calendar for scheduled content
calendar.forEach(item => {
  const calItem = item.json;
  if (calItem.date === todayStr && calItem.status !== 'posted') {
    postsToCreate.push({
      type: calItem.content_type || 'scheduled',
      topic: calItem.topic,
      details: calItem.details,
      hashtags: calItem.hashtags,
      imageRequired: calItem.needs_image === 'yes',
      priority: 'high',
      source: 'calendar'
    });
  }
});

// Generate content based on business type and time of day
if (postsToCreate.length === 0) {
  // Restaurant/Cafe specific
  if (clientConfig.businessType === 'restaurant' || clientConfig.businessType === 'cafe') {
    if (hour === 8) {
      postsToCreate.push({
        type: 'daily_special',
        topic: 'breakfast_lunch_special',
        details: 'morning offerings',
        imageRequired: true,
        priority: 'high'
      });
    } else if (hour === 12) {
      postsToCreate.push({
        type: 'lunch_reminder',
        topic: 'lunch specials and availability',
        imageRequired: false,
        priority: 'medium'
      });
    } else if (hour === 16) {
      postsToCreate.push({
        type: 'dinner_preview',
        topic: 'dinner specials or happy hour',
        imageRequired: true,
        priority: 'high'
      });
    }
  }

  // Retail/E-commerce specific
  else if (clientConfig.businessType === 'retail' || clientConfig.businessType === 'ecommerce') {
    if (dayOfWeek === 1 && hour === 8) { // Monday morning
      postsToCreate.push({
        type: 'weekly_feature',
        topic: 'featured products for the week',
        imageRequired: true,
        priority: 'high'
      });
    } else if (dayOfWeek === 5 && hour === 12) { // Friday noon
      postsToCreate.push({
        type: 'weekend_sale',
        topic: 'weekend promotions',
        imageRequired: true,
        priority: 'high'
      });
    } else if (hour === 16) {
      postsToCreate.push({
        type: 'product_highlight',
        topic: 'product benefits and features',
        imageRequired: true,
        priority: 'medium'
      });
    }
  }

  // Service business specific
  else if (clientConfig.businessType === 'service') {
    if (dayOfWeek === 1 && hour === 8) { // Monday
      postsToCreate.push({
        type: 'weekly_tip',
        topic: 'helpful tip for the week',
        imageRequired: false,
        priority: 'medium'
      });
    } else if (dayOfWeek === 3 && hour === 12) { // Wednesday
      postsToCreate.push({
        type: 'testimonial',
        topic: 'customer success story',
        imageRequired: false,
        priority: 'medium'
      });
    } else if (dayOfWeek === 5 && hour === 8) { // Friday
      postsToCreate.push({
        type: 'weekend_availability',
        topic: 'weekend service availability',
        imageRequired: false,
        priority: 'high'
      });
    }
  }

  // Professional services specific
  else if (clientConfig.businessType === 'professional') {
    if (dayOfWeek === 2 && hour === 8) { // Tuesday
      postsToCreate.push({
        type: 'thought_leadership',
        topic: 'industry insights',
        imageRequired: false,
        priority: 'medium'
      });
    } else if (dayOfWeek === 4 && hour === 12) { // Thursday
      postsToCreate.push({
        type: 'case_study',
        topic: 'client success or project highlight',
        imageRequired: false,
        priority: 'medium'
      });
    }
  }

  // Fitness/Gym specific
  else if (clientConfig.businessType === 'fitness' || clientConfig.businessType === 'gym') {
    if (hour === 8) {
      postsToCreate.push({
        type: 'motivation',
        topic: 'daily motivation',
        imageRequired: false,
        priority: 'high'
      });
    } else if (hour === 12) {
      postsToCreate.push({
        type: 'class_schedule',
        topic: 'today\'s classes',
        imageRequired: false,
        priority: 'medium'
      });
    } else if (hour === 16) {
      postsToCreate.push({
        type: 'workout_tip',
        topic: 'exercise tip or technique',
        imageRequired: true,
        priority: 'medium'
      });
    }
  }

  // Default/General business
  else {
    if (hour === 8 || hour === 12 || hour === 16) {
      const contentRotation = [
        { type: 'announcement', topic: 'business update' },
        { type: 'educational', topic: 'helpful information' },
        { type: 'engagement', topic: 'question or poll' },
        { type: 'promotional', topic: 'product or service highlight' },
        { type: 'team_highlight', topic: 'team member spotlight' },
        { type: 'community', topic: 'local community involvement' }
      ];

      // Rotate through content types
      const contentIndex = Math.floor(now.getTime() / (1000 * 60 * 60 * 24)) % contentRotation.length;
      postsToCreate.push({
        ...contentRotation[contentIndex],
        imageRequired: false,
        priority: 'medium'
      });
    }
  }
}

// Match available images if needed
postsToCreate.forEach(post => {
  if (post.imageRequired && availableImages.length > 0) {
    // Find unused image
    const unusedImage = availableImages.find(img => 
      !img.json.name.includes('_used') && 
      img.json.mimeType.startsWith('image/')
    );
    if (unusedImage) {
      post.imageUrl = unusedImage.json.webViewLink;
      post.imageThumbnail = unusedImage.json.thumbnailLink;
      post.imageId = unusedImage.json.id;
    }
  }
});

// Add business context to all posts
postsToCreate.forEach(post => {
  post.businessType = clientConfig.businessType;
  post.tone = clientConfig.tone;
  post.timestamp = now.toISOString();
});

return postsToCreate.map(post => ({ json: post }));

