// Enhanced platform-specific content preparation
const posts = $json;
const preparedPosts = [];

posts.forEach(post => {
  const now = new Date();
  const scheduledTime = new Date(post.scheduled_time || post.timestamp);

  // Check if it's time to post (within 30-minute window or immediate)
  const timeDiff = Math.abs(now.getTime() - scheduledTime.getTime());
  const thirtyMinutes = 30 * 60 * 1000;

  if (timeDiff <= thirtyMinutes || post.publish_immediately === 'true') {
    // Parse platforms and prepare content for each
    const platforms = (post.platforms || 'instagram,facebook,twitter').split(',').map(p => p.trim());
    const content = post.content.trim();
    const hashtags = post.hashtags || '';
    const imageUrl = post.image_url || null;
    const imageId = post.image_id || null;

    // Business-specific enhancements
    const businessType = post.business_type || 'general';
    let enhancedContent = content;

    // Add business-specific CTAs if not present
    if (!content.includes('üìû') && !content.includes('üîó') && !content.includes('Visit')) {
      switch(businessType) {
        case 'restaurant':
        case 'cafe':
          if (!content.includes('Order') && !content.includes('Reserve')) {
            enhancedContent += '\nüìû Call for reservations';
          }
          break;
        case 'retail':
        case 'ecommerce':
          if (!content.includes('Shop') && !content.includes('Buy')) {
            enhancedContent += '\nüõçÔ∏è Shop now - link in bio';
          }
          break;
        case 'service':
          if (!content.includes('Book') && !content.includes('Schedule')) {
            enhancedContent += '\nüìÖ Book your appointment today';
          }
          break;
        case 'professional':
          if (!content.includes('Learn') && !content.includes('Contact')) {
            enhancedContent += '\nüíº Learn more - link in bio';
          }
          break;
        case 'fitness':
        case 'gym':
          if (!content.includes('Join') && !content.includes('Sign up')) {
            enhancedContent += '\nüí™ Join us today!';
          }
          break;
      }
    }

    // Prepare platform-specific versions
    const platformVersions = {};

    // Instagram version
    if (platforms.includes('instagram')) {
      platformVersions.instagram = {
        content: enhancedContent + (hashtags ? '\n\n' + hashtags : ''),
        imageUrl: imageUrl,
        imageId: imageId,
        requiresImage: true, // Instagram works best with images
        type: post.post_type || 'feed'
      };
    }

    // Facebook version
    if (platforms.includes('facebook')) {
      platformVersions.facebook = {
        content: enhancedContent, // Facebook: hashtags less important
        imageUrl: imageUrl,
        imageId: imageId,
        link: post.link_url || null,
        type: post.post_type || 'feed'
      };
    }

    // Twitter/X version
    if (platforms.includes('twitter')) {
      // Twitter has 280 character limit
      let twitterContent = enhancedContent;
      if (twitterContent.length > 260) { // Leave room for link
        twitterContent = twitterContent.substring(0, 257) + '...';
      }
      platformVersions.twitter = {
        content: twitterContent + (hashtags ? ' ' + hashtags.split(' ').slice(0, 3).join(' ') : ''),
        imageUrl: imageUrl,
        imageId: imageId,
        type: 'tweet'
      };
    }

    // LinkedIn version (if applicable)
    if (platforms.includes('linkedin')) {
      platformVersions.linkedin = {
        content: enhancedContent + (hashtags ? '\n\n' + hashtags : ''),
        imageUrl: imageUrl,
        imageId: imageId,
        type: 'post',
        professional: true
      };
    }

    // Google Business version (if applicable)
    if (platforms.includes('google')) {
      platformVersions.google = {
        content: enhancedContent.substring(0, 1500), // Google has 1500 char limit
        imageUrl: imageUrl,
        imageId: imageId,
        type: 'update',
        callToAction: post.google_cta || 'LEARN_MORE'
      };
    }

    preparedPosts.push({
      originalPost: post,
      timestamp: post.timestamp,
      businessType: businessType,
      priority: post.priority || 'medium',
      platformVersions: platformVersions,
      platforms: platforms,
      hasImage: !!imageUrl,
      imageNeedsDownload: imageUrl && imageUrl.includes('drive.google.com'),
      publishTime: now.toISOString()
    });
  }
});

// Sort by priority (high > medium > low)
const priorityOrder = { high: 0, medium: 1, low: 2 };
preparedPosts.sort((a, b) => priorityOrder[a.priority] - priorityOrder[b.priority]);

// Limit to 5 posts per run to avoid rate limits
return preparedPosts.slice(0, 5).map(post => ({ json: post }));

// Combine results from all platforms
const originalData = $('prepare-platform-content').item;
const results = {
  timestamp: originalData.timestamp,
  publishTime: new Date().toISOString(),
  success: [],
  failed: [],
  platforms: {}
};

// Check each platform result
const platformNodes = [
  { name: 'instagram', node: 'post-to-instagram' },
  { name: 'facebook', node: 'post-to-facebook' },
  { name: 'twitter', node: 'post-to-twitter' },
  { name: 'linkedin', node: 'post-to-linkedin' },
  { name: 'google', node: 'post-to-google' }
];

platformNodes.forEach(platform => {
  try {
    const result = $(platform.node).first()?.json;
    if (result && !result.error) {
      results.success.push(platform.name);
      results.platforms[platform.name] = {
        status: 'success',
        postId: result.id || result.data?.id,
        url: result.permalink || result.url
      };
    } else if (result) {
      results.failed.push(platform.name);
      results.platforms[platform.name] = {
        status: 'failed',
        error: result.error?.message || 'Unknown error'
      };
    }
  } catch (error) {
    // Platform wasn't attempted
  }
});

results.overallStatus = results.failed.length === 0 ? 'success' : 
                        results.success.length > 0 ? 'partial' : 'failed';

return [{
  json: {
    ...originalData.originalPost,
    publishResults: results
  }
}];

